function get_canvas(){
    //Grab window dimensions
	var canvas = 'desktop';
	var canvasW = document.body.clientWidth;
	if (canvasW >= 1024) canvas = 'desktop';
	if (canvasW < 1024 && canvasW >= 768 ) canvas = 'tablet';
	if (canvasW < 768 && canvasW >= 480 ) canvas = 'mobile';
	if (canvasW < 480 ) canvas = 'portrait';
	
	return canvas;
}


//Debounce function - so a function doesn't get called a million times
(function($,sr){

  // debouncing function from John Hann
  // http://unscriptable.com/index.php/2009/03/20/debouncing-javascript-methods/
  var debounce = function (func, threshold, execAsap) {
      var timeout;

      return function debounced () {
          var obj = this, args = arguments;
          function delayed () {
              if (!execAsap)
                  func.apply(obj, args);
              timeout = null;
          };

          if (timeout)
              clearTimeout(timeout);
          else if (execAsap)
              func.apply(obj, args);

          timeout = setTimeout(delayed, threshold || 100);
      };
  }
  // smartresize 
  jQuery.fn[sr] = function(fn){  return fn ? this.bind('resize', debounce(fn)) : this.trigger(sr); };

})(jQuery,'smartresize');

